DROP TABLE IF EXISTS `cms_article`;
CREATE TABLE `cms_article`
(
    `id`           bigint(20) NOT NULL AUTO_INCREMENT COMMENT '主键id',
    `title`        varchar(128) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci DEFAULT NULL COMMENT '文章标题',
    `author`       varchar(45) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci  DEFAULT NULL COMMENT '作者',
    `column_id`    bigint(20)                                              DEFAULT NULL COMMENT '文章所属栏目id',
    `sort`         int(11)                                                 DEFAULT NULL COMMENT '排序 数值越小 排序越前',
    `is_release`   char(1) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci      DEFAULT '0' COMMENT '是否发布 0 发布 1 不发布  默认0 ',
    `seo_keywords` varchar(128) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci DEFAULT NULL COMMENT 'seo关键字',
    `seo_desc`     varchar(256) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci DEFAULT NULL COMMENT 'see描述',
    `desc`         text CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci COMMENT '文章描述',
    `del_flag`     char(1) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci      DEFAULT NULL COMMENT '删除标记 0 未删除 1 删除',
    `create_time`  datetime                                            DEFAULT NULL COMMENT '生成时间',
    `modify_time`  datetime                                            DEFAULT NULL COMMENT '修改时间',
    `del_time`     datetime                                            DEFAULT NULL COMMENT '删除时间',
    PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB
  AUTO_INCREMENT = 2
  CHARACTER SET = utf8mb4
  COLLATE = utf8mb4_general_ci COMMENT = '文章信息'
  ROW_FORMAT = Dynamic;

