DROP TABLE IF EXISTS `ls_district`;
CREATE TABLE `ls_district`
(
    `id`          bigint(20)                                             NOT NULL AUTO_INCREMENT COMMENT '主键id',
    `name`        varchar(45) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL COMMENT '区名称',
    `city_id`     bigint(20)                                             NOT NULL COMMENT '对应ls_city表中的id',
    `sort`        int(11)                                                         DEFAULT NULL COMMENT '排序 数值越小 越前',
    `del_flag`    char(1) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci     NOT NULL DEFAULT '0' COMMENT '删除标记  0 未删除 1删除  默认0',
    `create_time` datetime                                                    DEFAULT NULL COMMENT '创建时间',
    `modify_time` datetime                                                    DEFAULT NULL COMMENT '修改时间',
    `del_time`    datetime                                                    DEFAULT NULL COMMENT '删除时间',
    PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB
  AUTO_INCREMENT = 3106
  CHARACTER SET = utf8mb4
  COLLATE = utf8mb4_general_ci COMMENT = '区域区表'
  ROW_FORMAT = Dynamic;

