-- ----------------------------
-- Table structure for oms_back_order
-- ----------------------------
DROP TABLE IF EXISTS `oms_back_order`;
CREATE TABLE `oms_back_order`
(
    `id`                 bigint(20)                                              NOT NULL AUTO_INCREMENT COMMENT '主键id',
    `back_code`          varchar(128) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL COMMENT '退单号',
    `order_id`           bigint(20)                                              NOT NULL COMMENT '订单id 对应订单表 oms_order中的id',
    `order_code`         varchar(128) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL COMMENT '订单号 对应oms_order 表中的order_code\n',
    `store_id`           bigint(20)                                              NOT NULL COMMENT '店铺id 平台的为0 ',
    `customer_id`        bigint(20)                                              NOT NULL COMMENT '用户id',
    `skuId_nums`         varchar(512) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci  DEFAULT NULL COMMENT '退货的单品ID多个用,分开  退款的时候不需要，因为退款只能整单退\n1001-1,1002-3 表示单品id为1001 的单品退货1件 单品id为1002的单品退货3件 ',
    `type`               char(1) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci      NOT NULL COMMENT '1 退款 2 退货',
    `reason`             varchar(40) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci   DEFAULT NULL COMMENT '退款／退货原因\n1:不想买了\n2:收货人信息有误\n3:未按指定时间发货\n4:其他\n5:不想买了\n6:商品质量问题\n7:收到商品与描述不符\n8:其他\n9:系统自动申请\n',
    `desc`               varchar(2048) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci DEFAULT NULL COMMENT '问题说明',
    `credential`         char(1) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci       DEFAULT '0' COMMENT '申请凭据 0 没有任何凭据 1 有发票 2有质检报告',
    `back_type`          char(1) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci       DEFAULT NULL COMMENT '返回方式 1 快递返回 目前只有快递返回 （退货的时候用户给商城寄送商品）',
    `back_price`         decimal(20, 2)                                           DEFAULT NULL COMMENT '退款／退货金额',
    `real_back_price`    decimal(20, 2)                                           DEFAULT NULL COMMENT '退货时候实际退款金额',
    `pics`               varchar(2048) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci DEFAULT NULL COMMENT '上传的退款凭证或者质检发票 多个图片 用, 隔开',
    `status`             char(4) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci      NOT NULL COMMENT '退款／退货状态  \n1:退款申请 （用户发送退款请求）\n2:退款成功（商家同意退款）\n3:退款拒绝 （商家拒绝退款）\n4:退货申请 （用户发起退货请求）\n5:退货拒绝   （商家拒绝退货）\n6:退货审核通过等待用户填写物流（商家审核通过，等待用户寄回商品）\n7: 待收货  （用户已经寄回商品，等待商家收货确认）\n8：退货完成（商家收货并且同意退款给用户）\n9:退货失败（商家不同意退款）',
    `predeposit_pay`     char(1) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci       DEFAULT '0' COMMENT '是否预存款支付  0 否 1 是  默认0',
    `logis_company_name` varchar(45) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci   DEFAULT NULL COMMENT '物流公司名称',
    `waybill_code`       varchar(128) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci  DEFAULT NULL COMMENT '物流单号',
    `create_time`        datetime                                             DEFAULT NULL COMMENT '生成时间',
    PRIMARY KEY (`id`) USING BTREE,
    INDEX `store_id` (`store_id`) USING BTREE,
    INDEX `customer_id` (`customer_id`) USING BTREE
) ENGINE = InnoDB
  AUTO_INCREMENT = 49
  CHARACTER SET = utf8mb4
  COLLATE = utf8mb4_general_ci COMMENT = '退单退款表'
  ROW_FORMAT = Dynamic;

