-- ----------------------------
-- Table structure for oms_order
-- ----------------------------
DROP TABLE IF EXISTS `oms_order`;
CREATE TABLE `oms_order`
(
    `id`                        bigint(20)                                             NOT NULL AUTO_INCREMENT COMMENT '主键id',
    `order_code`                varchar(45) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL COMMENT '订单code',
    `master_order_code`         varchar(45) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL COMMENT '主订单code  下单可能会同时下多个订单 ，一个master_order_code 可以对应多个order_code 即多个订单',
    `customer_id`               bigint(20)                                             NOT NULL COMMENT '订单所属的会员id',
    `price`                     decimal(20, 2)                                         NOT NULL COMMENT '订单的最终价格(订单的最终成交价格)',
    `presale_price`             decimal(20, 2)                                         DEFAULT NULL COMMENT '预售需要支付的金额 （预售订单使用）',
    `original_price`            decimal(20, 2)                                         DEFAULT NULL COMMENT '订单原始金额（每个单品详情页面的总价）',
    `freight_price`             decimal(20, 2)                                         NOT NULL COMMENT '运费',
    `modify_price`              decimal(20, 2)                                         DEFAULT 0.00 COMMENT '订单修改的金额（后端管理员修改）',
    `point_price`               decimal(20, 2)                                         DEFAULT NULL COMMENT '积分抵消的金额',
    `coupon_price`              decimal(20, 2)                                         DEFAULT NULL COMMENT '优惠卷抵消的价格',
    `red_envelope_price`        decimal(20, 2)                                         DEFAULT NULL COMMENT '使用红包减去的价格',
    `concessional_rate`         decimal(20, 2)                                         DEFAULT NULL COMMENT '总的优惠价（包含订单使用的优惠卷 积分 红包 ,满减满折的价格）',
    `status`                    char(2) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci     NOT NULL COMMENT '1:待付款  （用户刚下单）\r2:代发货  （用户付完款 等待商城发货）\r3:代收货  （商城已经发货 等待用户确认收货）\r4:已完成  （用户已经确认收货 订单结束）\r5:取消订单 （用户未付款前取消订单）\r6:退款通过  （用户已经付款但是商城还未发货，用户发出退款申请，商城同意退款）\r7:退货通过   （用户已经确认收货后用户发出退货申请，商城同意所有退货申请 ，一个订单可能有多个单品）\r\n',
    `presale_status`            char(1) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci     DEFAULT '0' COMMENT '预售订单状态   普通订单 该状态没作用\n0 第一阶段未支付\n1 第一阶段已支付第二阶段未支付\n2 第二阶段已支付   默认0 ',
    `evaluation_status`         char(1) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci     DEFAULT '0' COMMENT '评价状态  0 未评价 1 已评价  默认为0 未评价',
    `red_envelope_code`         varchar(64) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci DEFAULT NULL COMMENT '使用红包的code',
    `coupon_no`                 varchar(64) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci DEFAULT NULL COMMENT '使用的优惠卷的卷码',
    `use_point`                 int(11)                                                DEFAULT NULL COMMENT '使用的积分数量',
    `pay_type`                  char(1) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci     NOT NULL COMMENT '支付类型  0在线支付  1货到付款 ',
    `store_id`                  bigint(20)                                             NOT NULL COMMENT '订单店铺id  平台的订单id为0',
    `cancel_reson`              char(1) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci     DEFAULT NULL COMMENT '订单取消原因\n1:现在不想买\n2:商品价格较贵\n3:价格波动\n4:商品缺货\n5:重复下单\n6:收货人信息有误\n7:发票信息有误/发票未开\n8:送货时间过长\n9:其他原因\n0:系统取消',
    `predeposit_pay`            char(1) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci     DEFAULT '0' COMMENT '是否预存款支付  0 否 1 是  默认0',
    `source`                    char(1) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci     DEFAULT NULL COMMENT '订单来源 1pc  2 h5   3 app',
    `waybill_code`              varchar(45) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci DEFAULT NULL COMMENT '运单号',
    `order_type`                char(1) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci     DEFAULT '0' COMMENT '订单类型 0 普通订单 1 定金预售订单 2全款预售订单 3 拼团订单 4 众筹全款 5 众筹1元 6 众筹无回报 7 虚拟商品订单 8 社区团购订单 默认0 普通订单',
    `create_time`               datetime                                           DEFAULT NULL COMMENT '创建时间',
    `pay_time`                  datetime                                           DEFAULT NULL COMMENT '支付时间',
    `delivery_time`             datetime                                           DEFAULT NULL COMMENT '发货时间',
    `receiving_time`            datetime                                           DEFAULT NULL COMMENT '确认收货时间',
    `cancel_time`               datetime                                           DEFAULT NULL COMMENT '订单取消时间',
    `modify_time`               datetime                                           DEFAULT NULL COMMENT '修改时间',
    `evaluation_time`           datetime                                           DEFAULT NULL COMMENT '订单评价时间',
    `presale_time`              datetime                                           DEFAULT NULL COMMENT '预售订单的时间\n对于定金预售 则是第二阶段付款时间 和发货时间 对于全款预售 则是发货时间',
    `group_head`                char(1) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci     DEFAULT NULL COMMENT '是否团长  0 是 1 否  当订单为拼团订单的时候有用',
    `group_id`                  varchar(45) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci DEFAULT NULL COMMENT '拼团订单的id  拼团订单的时候有用',
    `group_marketing_id`        bigint(20)                                             DEFAULT NULL COMMENT '拼团订单的促销id 拼团订单的时候有用',
    `group_sku_id`              varchar(64) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci DEFAULT NULL COMMENT '拼团的单品id （拼团订单时候有效）',
    `group_status`              char(2) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci     DEFAULT '-1' COMMENT '拼团状态 -1无状态 0未成团 1已成团 默认-1 ',
    `group_num`                 int(11)                                                DEFAULT NULL COMMENT '成团的数量',
    `open_group_time`           datetime(0)                                            DEFAULT NULL COMMENT '开团时间',
    `auto_handle_status`        char(1) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci     DEFAULT '0' COMMENT '拼团订单定时任务处理状态 \'0\'未处理 \'1\'已处理 默认\'0\'',
    `recommended`               bigint(20)                                             DEFAULT -1 COMMENT '订单推广人的会员id(订单会员的推广人 ，也就是订单会员的上级的id) 如果为-1 则说明该订单会员没有推荐人或者不是推广订单',
    `s_recommended`             bigint(20)                                             DEFAULT -1 COMMENT '订单二级推广人的会员id(订单会员的二级推广人 ，也就是订单会员的上级的上级会员id) 如果为-1 则说明该订单会员没有推荐人或者不是推广订单',
    `crowdfunding_id`           bigint(20)                                             DEFAULT NULL COMMENT '众筹id  对应ls_markeing 表中的id',
    `lottery_status`            char(1) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci     DEFAULT '0' COMMENT '抽奖状态  0 待抽奖 1 抽中 默认0',
    `write_off_code`            varchar(10) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci DEFAULT NULL COMMENT '虚拟订单的核销码',
    `community_buy_customer_id` int(11)                                                DEFAULT NULL COMMENT '社区团购的团长会员id  社区团购的时候使用 对应ums_member表中的id',
    `community_buy_id`          int(11)                                                DEFAULT NULL COMMENT '社区团购的团购id  对应s_community_buy 表中的id ',
    `profit`                    decimal(20, 2)                                         DEFAULT NULL COMMENT '社区团购的利润，社区团购的时候使用',
    `community_name`            varchar(64) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci DEFAULT NULL COMMENT '小区名称 社区团购使用',
    `community_buy_name`        varchar(64) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci DEFAULT NULL COMMENT '社区团购名称',
