-- ----------------------------
-- Table structure for t_store_billing_records
-- ----------------------------
DROP TABLE IF EXISTS `t_store_billing_records`;
CREATE TABLE `t_store_billing_records`
(
    `id`          bigint(20) NOT NULL AUTO_INCREMENT COMMENT '主键id',
    `order_code`  varchar(64) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci DEFAULT NULL COMMENT '订单code',
    `type`        char(1) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci     DEFAULT NULL COMMENT '账单进出类型 0 进 1 出',
    `store_id`    bigint(20)                                             DEFAULT NULL COMMENT '店铺id',
    `store_name`  varchar(45) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci DEFAULT NULL COMMENT '店铺名称',
    `record_type` char(1) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci     DEFAULT NULL COMMENT '账单记录类型 1 提货完成 2 代客下单完成',
    `order_price` decimal(20, 2)                                         DEFAULT NULL COMMENT '订单价格',
    `create_time` datetime                                           DEFAULT NULL COMMENT '创建时间',
    `status`      char(1) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci     DEFAULT NULL COMMENT '结算状态 0 未结算 1 已结算 默认0 ',
    PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB
  AUTO_INCREMENT = 1
  CHARACTER SET = utf8mb4
  COLLATE = utf8mb4_general_ci COMMENT = '门店账单收入支出表'
  ROW_FORMAT = Dynamic;

-- ----------------------------
-- Table structure for t_store_category
-- ----------------------------
DROP TABLE IF EXISTS `t_store_category`;
CREATE TABLE `t_store_category`
(
    `id`          bigint(20)                                             NOT NULL AUTO_INCREMENT COMMENT '主键id',
    `name`        varchar(32) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL COMMENT '分类名称',
    `parent_id`   bigint(20)                                             NOT NULL COMMENT '上级分类id 0 表示没有上级',
    `grade`       int(11)                                                NOT NULL COMMENT '1 一级分类 2 二级分类 3 三级分类',
    `sort`        int(11)                                            DEFAULT NULL COMMENT '排序  数值越小 越靠前',
    `store_id`    bigint(20)                                         DEFAULT NULL COMMENT '店铺id',
    `del_flag`    char(1) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci DEFAULT NULL COMMENT '删除标记  0 未删除 1 删除',
    `create_time` datetime                                       DEFAULT NULL COMMENT '创建时间',
    `modify_time` datetime                                       DEFAULT NULL COMMENT '修改时间',
    `del_time`    datetime                                       DEFAULT NULL COMMENT '删除时间',
    PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB
  AUTO_INCREMENT = 1
  CHARACTER SET = utf8mb4
  COLLATE = utf8mb4_general_ci COMMENT = '店铺分类'
  ROW_FORMAT = Dynamic;

-- ----------------------------
-- Table structure for t_store_comment
-- ----------------------------
DROP TABLE IF EXISTS `t_store_comment`;
CREATE TABLE `t_store_comment`
(
    `id`              bigint(20) NOT NULL AUTO_INCREMENT COMMENT '主键id',
    `store_id`        bigint(20) NOT NULL COMMENT '店铺id',
    `order_id`        bigint(20)   DEFAULT NULL COMMENT '订单号',
    `customer_id`     bigint(20)   DEFAULT NULL COMMENT '会员id',
    `desc_score`      int(11)      DEFAULT NULL COMMENT '描述相符 评分1到5分',
    `seller_score`    int(11)      DEFAULT NULL COMMENT '卖家服务评分 1到5分',
    `logistics_score` int(11)      DEFAULT NULL COMMENT '物流评分 1到5分',
    `create_time`     datetime DEFAULT NULL COMMENT '创建时间',
    PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB
  AUTO_INCREMENT = 8
  CHARACTER SET = utf8mb4
  COLLATE = utf8mb4_general_ci COMMENT = '店铺评论表'
  ROW_FORMAT = Dynamic;

-- ----------------------------
-- Table structure for t_store_customize_brand
-- ----------------------------
DROP TABLE IF EXISTS `t_store_customize_brand`;
CREATE TABLE `t_store_customize_brand`
(
    `id`             int(11) NOT NULL AUTO_INCREMENT COMMENT '主键id',
    `name`           varchar(45) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci   DEFAULT NULL COMMENT '品牌名称',
    `url`            varchar(128) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci  DEFAULT NULL COMMENT '品牌的图片',
    `certificat_url` varchar(128) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci  DEFAULT NULL COMMENT '证书图片',
    `store_id`       char(1) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci       DEFAULT NULL COMMENT '店铺id',
    `brand_id`       bigint(20)                                               DEFAULT NULL COMMENT '对应品牌表中的id',
    `status`         char(1) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci       DEFAULT NULL COMMENT '状态  0 申请中  1通过 2 拒绝',
    `reason`         varchar(1024) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci DEFAULT NULL COMMENT '拒绝原因',
    `del_flag`       char(1) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci       DEFAULT '0' COMMENT '删除标记  0 未删除 1 删除默认0 ',
    `create_time`    datetime                                             DEFAULT NULL COMMENT '创建时间',
    PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB
  AUTO_INCREMENT = 1
  CHARACTER SET = utf8mb4
  COLLATE = utf8mb4_general_ci COMMENT = '店铺自定义品牌列表'
