DROP TABLE IF EXISTS `t_store_order`;
CREATE TABLE `t_store_order`
(
    `id`                 bigint(20) NOT NULL AUTO_INCREMENT COMMENT '主键id',
    `order_code`         varchar(45) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci  DEFAULT NULL COMMENT '订单编号',
    `master_order_code`  varchar(45) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci  DEFAULT NULL COMMENT '主订单code  下单可能会同时下多个订单 ，一个master_order_code 可以对应多个order_code 即多个订单',
