    `store_id`           bigint(20)                                              DEFAULT NULL COMMENT '店铺id',
    `evaluation_status`  char(1) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci      DEFAULT '0' COMMENT '评价状态  0 未评价 1 已评价  默认为0 未评价',
    `red_envelope_price` decimal(20, 2)                                          DEFAULT NULL COMMENT '使用红包减去的金额',
    `red_envelope_code`  varchar(64) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci  DEFAULT NULL COMMENT '红包的卷码',
    `write_off_code`     varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci DEFAULT NULL COMMENT '核销码',
    `source`             char(1) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci      DEFAULT NULL COMMENT '订单来源 1pc  2 h5   3 app 4 代客下单',
    `create_time`        datetime                                            DEFAULT NULL COMMENT '创建时间',
    `pay_time`           datetime                                            DEFAULT NULL COMMENT '支付时间',
    `cancel_time`        datetime                                            DEFAULT NULL COMMENT '取消时间',
    `cancel_reason`      char(2) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci      DEFAULT NULL COMMENT '订单取消原因 1 现在不想买 2商品价格较贵 3价格波动 4商品缺货 5 重复下单 6发票信息有误 7 其他原因 8 无法备齐货物 9不是有效订单 10 买家主动要求',
    `pick_up_time`       varchar(32) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci  DEFAULT NULL COMMENT '取货时间',
