    `pay_type`           varchar(16) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci  DEFAULT NULL COMMENT '支付类型',
    `predeposit_pay`     char(1) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci      DEFAULT '0' COMMENT '是否是预存款支付  0否1 是 默认0 ',
    PRIMARY KEY (`id`) USING BTREE,
    INDEX `customer_id` (`customer_id`) USING BTREE
) ENGINE = InnoDB
  AUTO_INCREMENT = 1
  CHARACTER SET = utf8mb4
  COLLATE = utf8mb4_general_ci COMMENT = '门店订单'
  ROW_FORMAT = Dynamic;

