    `relename`               varchar(45) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci           DEFAULT NULL COMMENT '真实姓名',
    `cardid`                 varchar(32) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci           DEFAULT NULL COMMENT '身份证',
    `gender`                 char(1) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci      NOT NULL DEFAULT '0' COMMENT '性别  0 保密 1男 2女 默认0',
    `birthday`               date                                                             DEFAULT NULL COMMENT '生日',
    `monthly_income`         char(1) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci               DEFAULT NULL COMMENT '月收入  0 无收入 1 2000以下 2 2000-3999 3 4000-5999\n4 6000－7999  5 8000以上',
    `marriage_status`        char(1) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci      NOT NULL DEFAULT '0' COMMENT '婚姻状况  0 保密 1未婚 2 已婚 默认0 保姆',
    `mobile`                 varchar(45) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci           DEFAULT NULL COMMENT '手机号码',
    `email`                  varchar(32) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci           DEFAULT NULL COMMENT '邮箱地址',
    `modified_email`         varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci          DEFAULT NULL COMMENT '更改中的邮箱（绑定新邮箱使用）',
    `check_code`             varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci          DEFAULT NULL COMMENT '校验码（绑定新邮箱时使用）',
    `province`               bigint(20)                                                       DEFAULT NULL COMMENT '省',
    `city`                   bigint(20)                                                       DEFAULT NULL COMMENT '市',
    `county`                 bigint(20)                                                       DEFAULT NULL COMMENT '区',
    `detailaddress`          varchar(128) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci          DEFAULT NULL COMMENT '详细地址',
    `interest`               varchar(2048) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci         DEFAULT NULL COMMENT '兴趣爱好',
    `store_id`               bigint(20)                                                       DEFAULT 0 COMMENT '店铺id  平台的为0  默认为平台',
    `type`                   char(1) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci               DEFAULT NULL COMMENT '1 普通用户 2 商家店铺用户 3 店铺员工',
    `consumption_amount`     decimal(20, 2)                                                   DEFAULT NULL COMMENT '总的消费金额',
    `paypassword`            varchar(256) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci          DEFAULT NULL COMMENT '预存款支付密码',
    `source`                 char(1) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci               DEFAULT NULL COMMENT '用户注册来源 1 pc  2app  3 手机h5 4 管理员后台新增\n',
    `status`                 char(1) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci               DEFAULT '1' COMMENT '用户状态 1 正常 2 冻结  3 未启用 默认1 ',
    `sign_num`               int(11)                                                          DEFAULT 0 COMMENT '会员签到次数',
    `is_mobile_verification` char(1) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci               DEFAULT '0' COMMENT '手机是否验证  0 否 1 验证 默认0 ',
    `is_email_verification`  char(1) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci               DEFAULT '0' COMMENT '邮箱是否验证   0 否 1 验证  默认0  ',
    `login_error_count`      int(11)                                                          DEFAULT NULL COMMENT '错误登录的次数',
    `del_flag`               char(1) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci               DEFAULT '0' COMMENT '删除标记 0 未删除 1删除 默认0 ',
    `create_time`            datetime                                                     DEFAULT NULL COMMENT '创建时间',
    `modify_time`            datetime                                                     DEFAULT NULL COMMENT '修改时间',
    `del_time`               datetime                                                     DEFAULT NULL COMMENT '删除时间',
    `lock_time`              datetime                                                     DEFAULT NULL COMMENT '锁定时间',
    `last_login_time`        datetime                                                     DEFAULT NULL COMMENT '最近登陆时间',
    `recommended`            bigint(20)                                                       DEFAULT -1 COMMENT '推荐人的会员id  如果没有 则为-1  默认为-1',
    `s_recommended`          bigint(20)                                                       DEFAULT -1 COMMENT '二级推荐人的会员id （上级的上级） 如果没有 则为-1  默认为-1',
    `self_recommend_code`    varchar(45) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci           DEFAULT NULL COMMENT '会员自己的推荐码',
    `commission`             decimal(20, 2)                                                   DEFAULT 0.00 COMMENT '会员的佣金',
    `belone_store_id`        bigint(20)                                                       DEFAULT 0 COMMENT '会员所属的店铺id',
    `applet_open_id`         varchar(55) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci           DEFAULT NULL,
    `app_open_id`            varchar(55) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci           DEFAULT NULL,
    `h5_open_id`             varchar(55) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci           DEFAULT NULL,
    PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB
  AUTO_INCREMENT = 1080
  CHARACTER SET = utf8mb4mb4
  COLLATE = utf8mb4_bin COMMENT = '会员表'
  ROW_FORMAT = Dynamic;


