mapper.update:
update f_system_args set
  fvalue = #{fvalue,jdbcType=VARCHAR}
where fkey = #{fkey,jdbcType=VARCHAR}

mapper.select:
select fid, fkey, fvalue, fdescription, version,ftype, furl
from f_system_args
where 1=1

  and	(
  fkey like concat('%',#{keyword,jdbcType=VARCHAR},'%') or
  fvalue like concat('%',#{keyword,jdbcType=VARCHAR},'%')
  )


  and ftype = #{ftype}

order by ${orderfield} ${orderdirection}
limit #{offset},#{limit}

select count(fid) from f_system_args where 1=1
and ( fkey like concat('%',#{keyword,jdbcType=VARCHAR},'%') or fvalue like concat('%',#{keyword,jdbcType=VARCHAR},'%') )
and ftype = #{ftype}
