mapper.select:
SELECT h.id,h.`name`,h.sort,h.help_cate_id,h.is_show,h.`desc`,h.del_flag,h.crete_time,h.modify_time,h.del_time,hc.name as cname FROM cms_help_cate hc JOIN cms_help h ON hc.id = h.help_cate_id WHERE h.del_flag = '0'
AND h.`name` LIKE CONCAT(CONCAT('%',#{name}),'%')
ORDER BY sort limit #{startRowNum},#{pageSize}

mapper.update:
UPDATE cms_help
SET del_flag = '1',del_time = now()
WHERE id = #{id}

UPDATE cms_help
SET `name` = #{name},sort = #{sort},help_cate_id = #{helpCateId},is_show = #{isShow},`desc` = #{desc},modify_time = now()
WHERE id = #{id}
