mapper.select:
select * from pms_sku
where del_flag = '0'

    and store_id = #{storeId}


    AND name like CONCAT(CONCAT('%', #{name}),'%')


    AND sku_no = #{skuNo}



    AND id = #{id}



    AND commission_rate > #{commissionRate}

order by create_time desc
limit #{startRowNum},#{pageSize}

select count(1) from pms_sku
where del_flag = '0'

    and store_id = #{storeId}


    AND name like CONCAT(CONCAT('%', #{name}),'%')


    AND sku_no = #{skuNo}



    AND id = #{id}



    AND commission_rate > #{commissionRate}

mapper.update:
update pms_sku
spu_id = #{spuId}, sku_no = #{skuNo}, name = #{name}, subtitle = #{subtitle}, stock = #{stock}, warning_stock = #{warningStock}, price = #{price}, weight = #{weight}, store_id = #{storeId}, shelves_status = #{shelvesStatus}, url = #{url}, `status` = #{status}, reason = #{reason}, del_flag = #{delFlag}, create_name = #{createName}, modify_name = #{modifyName}, del_name = #{delName}, create_time = #{createTime}, modify_time = #{modifyTime}, del_time = #{delTime}, up_time = #{upTime}, commission_rate = #{commissionRate}, s_commission_rate = #{sCommissionRate}, is_virtual = #{isVirtual}, is_dropship = #{isDropship}, dropship_commission_rate = #{dropshipCommissionRate}, is_batch_sku = #{isBatchSku}, logistics_template_id = #{logisticsTemplateId},
where id = #{id}

mapper.select:
select  pms_sku.*  from sms_marketing join pms_sku_marketing on sms_marketing.id = pms_sku_marketing.marketing_id
 join pms_sku on pms_sku_marketing.skuid =pms_sku.id  and sms_marketing.store_id = #{storeId} and sms_marketing. pms_sku.shelves_status = '1' and pms_sku.  sms_marketing.starttime <= now() and  sms_marketing.endtime > now()
  group by pms_sku.id limit #{startRowNum},#{pageSize}

mapper.update:
update pms_sku set shelves_status = #{status}
where store_id in

    #{item}
