mapper.update:
update sms_marketing_group set group_num = #{groupNum},limit_num = #{limitNum},price=#{price},
rule=#{rule}
where id = #{id}

mapper.delete:
delete from sms_marketing_group where marketing_id = #{marketingId}
and id in

    #{item}

mapper.select:
select lmg.*,lm.store_id from sms_marketing_group lmg join sms_marketing lm on lm.id = lmg.marketing_id join pms_sku ls on ls.id = lmg.skuid where now() >= lm.starttime and now() < lm.endtime and (select count(1) as num from sms_marketing_group_show lmgs where lmgs.group_id = lmg.id and lmgs.store_id = #{storeId} and lmgs.del_flag = '0') = 0
and lm.store_id = #{storeId}
and ls.name like CONCAT(CONCAT('%', #{name}),'%')
and ls.sku_no = #{skuNo}
order by lmg.id desc limit #{startRowNum},#{pageSize}

select count(1) from sms_marketing_group lmg join sms_marketing lm on lm.id = lmg.marketing_id join pms_sku ls on ls.id = lmg.skuid where now() >= lm.starttime and now() < lm.endtime and (select count(1) as num from sms_marketing_group_show lmgs where lmgs.group_id = lmg.id and lmgs.store_id = #{storeId} and lmgs.del_flag = '0') = 0
and lm.store_id = #{storeId}
and ls.name like CONCAT(CONCAT('%', #{name}),'%')
and ls.sku_no = #{skuNo}

select lmg.* from sms_marketing_group lmg
join sms_marketing lm on lm.id = lmg.marketing_id
where lmg.id = #{id}

    and lm.store_id = #{storeId}
