mapper.update:
update sms_marketing_presale_show set del_flag = '1' where id in

    #{id}

and store_id = #{storeId}

mapper.select:
select lmps.*,lm.store_id old_store_id,lmp.skuid,lm.starttime,lm.endtime from sms_marketing_presale_show lmps
join sms_marketing_presale lmp on lmp.id = lmps.presale_id
join sms_marketing lm on lm.id = lmp.marketing_id
join pms_sku ls on ls.id = lmp.skuid
where  lmps.store_id = #{storeId}

    and ls.name like CONCAT(CONCAT('%', #{name}),'%')


    and ls.sku_no = #{skuNo}

order by lmps.id desc
limit #{startRowNum},#{pageSize}

select count(1) from sms_marketing_presale_show lmps join sms_marketing_presale lmp on lmp.id = lmps.presale_id join sms_marketing lm on lm.id = lmp.marketing_id join pms_sku ls on ls.id = lmp.skuid where lmps.store_id = #{storeId}
and ls.name like CONCAT(CONCAT('%', #{name}),'%')
and ls.sku_no = #{skuNo}

select lmps.*,lm.store_id old_store_id,lmp.skuid,lm.starttime,lm.endtime from sms_marketing_presale_show lmps
join sms_marketing_presale lmp on lmp.id = lmps.presale_id
join sms_marketing lm on lm.id = lmp.marketing_id
join pms_sku ls on ls.id = lmp.skuid
where
 lmps.store_id = 0 and lm.store_id = #{storeId}

    and ls.name like CONCAT(CONCAT('%', #{name}),'%')


    and ls.sku_no = #{skuNo}

order by lmps.id desc
limit #{startRowNum},#{pageSize}

select count(1) from sms_marketing_presale_show lmps join sms_marketing_presale lmp on lmp.id = lmps.presale_id join sms_marketing lm on lm.id = lmp.marketing_id join pms_sku ls on ls.id = lmp.skuid where lmps.store_id = 0 and lm.store_id = #{storeId}
and ls.name like CONCAT(CONCAT('%', #{name}),'%')
and ls.sku_no = #{skuNo}

select lmps.*,lm.starttime,lm.endtime from sms_marketing_presale_show lmps
join sms_marketing_presale lmp on lmp.id = lmps.presale_id
join sms_marketing lm on lm.id = lmp.marketing_id
join pms_sku ls on ls.id = lmp.skuid
where
 now() >= lm.starttime and now() < lm.endtime and lmps.store_id = #{storeId}

    and lmps.cate_id = #{cateId}

order by lmps.sort
limit #{startRowNum},#{pageSize}
