mapper.update:
update sms_marketing_try set  `type` = #{type}, try_num = #{tryNum},apply_num = #{applyNum},coupon_id=#{couponId},
sku_info=#{skuInfo}
where id = #{id}

mapper.delete:
delete from sms_marketing_try where marketing_id = #{marketingId}
and id in

    #{item}

mapper.select:
select lmt.*,lm.store_id from sms_marketing_try lmt join sms_marketing lm on lm.id = lmt.marketing_id join pms_sku ls on ls.id = lmt.skuid where lm. ls. now() >= lm.starttime and now() < lm.endtime and (select count(1) as num from sms_marketing_try_show lmts where lmts.try_id = lmt.id and lmts.store_id = #{storeId} and lmts.del_flag = '0') = 0
and lm.store_id = #{storeId}
and ls.name like CONCAT(CONCAT('%', #{name}),'%')
and ls.sku_no = #{skuNo}
order by lmt.id desc limit #{startRowNum},#{pageSize}

select count(1) from sms_marketing_try lmt join sms_marketing lm on lm.id = lmt.marketing_id join pms_sku ls on ls.id = lmt.skuid where lm. ls. now() >= lm.starttime and now() < lm.endtime and (select count(1) as num from sms_marketing_try_show lmts where lmts.try_id = lmt.id and lmts.store_id = #{storeId} and lmts.del_flag = '0') = 0
and lm.store_id = #{storeId}
and ls.name like CONCAT(CONCAT('%', #{name}),'%')
and ls.sku_no = #{skuNo}

select lmt.*,lm.starttime,lm.endtime from sms_marketing_try lmt
join sms_marketing lm on lm.id = lmt.marketing_id
where lmt.id = #{id}

    and lm.store_id = #{storeId}
