mapper.select:
select lsr.*,lsi.store_name store_name,ls.`name` sku_name,ls.url url,lss.price price from t_store_reservation lsr join pms_sku ls on lsr.sku_id=ls.id join t_store_sku lss on lss.sku_id=lsr.sku_id and lss.store_id=lsr.store_id join t_store_info lsi on lsi.id=lsr.store_id where lsr. ls. ls.`status`='0' and ls.shelves_status='1' and lsr.customer_id=#{customerId}
and ls.`name` like CONCAT(CONCAT('%', #{skuName}),'%')
order by lsr.create_time desc limit #{startRowNum},#{pageSize}

mapper.update:
update t_store_reservation set del_flag='1' where
id in

    #{id}


    and customer_id = #{customerId}


    and store_id = #{storeId}

mapper.select:
