mapper.select:
SELECT id,`name`,num,limit_num,`type`,`desc`,store_id,starttime,endtime,use_type,del_flag
FROM sms_coupon WHERE del_flag='0' AND store_id=#{storeId}

    AND `name` LIKE CONCAT(CONCAT('%', #{name}),'%')

order by id desc
limit #{startRowNum},#{pageSize}

select lf.* from (SELECT lc.*, ls.store_name store_name FROM sms_coupon lc left JOIN (select lsi.store_name store_name,lsi.id id from t_store_info lsi where lsi.status='2' and lsi.effective_time > now() and lsi.del_flag='0') ls on lc.store_id=ls.id WHERE lc.del_flag='0' and lc.endtime > now() and lc.use_type='1' ) lf where (lf.store_id=0 || lf.store_name is not null)
and lf.store_id = #{storeId}
order by id desc limit #{startRowNum},#{pageSize}

SELECT id,`name`,num,limit_num,`type`,`desc`,store_id,starttime,endtime,use_type,del_flag
FROM sms_coupon WHERE del_flag='0' AND id=#{couponId}

    AND use_type=#{useType}

SELECT id,`name`,num,limit_num,`type`,`desc`,store_id,starttime,endtime,use_type,del_flag
FROM sms_coupon WHERE del_flag='0' AND store_id=#{storeId}

    AND use_type=#{useType}

AND starttime <= now() AND endtime > now()
