mapper.select:
SELECT id,`name`
FROM pms_combination WHERE  store_id = #{storeId}

    AND `name` LIKE CONCAT(CONCAT('%', #{name}),'%')

ORDER BY create_time DESC
limit #{startRowNum},#{pageSize}

SELECT COUNT(1)
FROM pms_combination WHERE  store_id = #{storeId}

    AND `name` LIKE CONCAT(CONCAT('%', #{name}),'%')

select count(1) from pms_combination JOIN pms_combination_sku on pms_combination.id = pms_combination_sku.combination_id where pms_combination_sku.sku_id IN
#{skuId}
and pms_combination.del_flag = '0'
