mapper.update:
update sms_marketing_group_show set del_flag = '1' where id in

    #{id}

and store_id = #{storeId}

mapper.select:
select lmgs.*,lm.store_id old_store_id,lmg.skuid,lm.starttime,lm.endtime from sms_marketing_group_show lmgs
join sms_marketing_group lmg on lmg.id = lmgs.group_id
join sms_marketing lm on lm.id = lmg.marketing_id
join pms_sku ls on ls.id = lmg.skuid
where lmgs.store_id = #{storeId}

    and ls.name like CONCAT(CONCAT('%', #{name}),'%')


    and ls.sku_no = #{skuNo}

order by lmgs.id desc
limit #{startRowNum},#{pageSize}

select count(1) from sms_marketing_group_show lmgs join sms_marketing_group lmg on lmg.id = lmgs.group_id join sms_marketing lm on lm.id = lmg.marketing_id join pms_sku ls on ls.id = lmg.skuid where lmgs.store_id = #{storeId}
and ls.name like CONCAT(CONCAT('%', #{name}),'%')
and ls.sku_no = #{skuNo}

select lmgs.*,lm.store_id old_store_id,lmg.skuid,lm.starttime,lm.endtime from sms_marketing_group_show lmgs
join sms_marketing_group lmg on lmg.id = lmgs.group_id
join sms_marketing lm on lm.id = lmg.marketing_id
join pms_sku ls on ls.id = lmg.skuid
where lmgs.del_flag ='0' and lm.del_flag='0' and ls.del_flag = '0'
and lmgs.store_id = 0 and lm.store_id = #{storeId}

    and ls.name like CONCAT(CONCAT('%', #{name}),'%')


    and ls.sku_no = #{skuNo}

order by lmgs.id desc
limit #{startRowNum},#{pageSize}

select count(1) from sms_marketing_group_show lmgs join sms_marketing_group lmg on lmg.id = lmgs.group_id join sms_marketing lm on lm.id = lmg.marketing_id join pms_sku ls on ls.id = lmg.skuid where lmgs.del_flag ='0' and lm.del_flag='0' and ls.del_flag = '0' and lmgs.store_id = 0 and lm.store_id = #{storeId}
and ls.name like CONCAT(CONCAT('%', #{name}),'%')
and ls.sku_no = #{skuNo}

select lmgs.* from sms_marketing_group_show lmgs
join sms_marketing_group lmg on lmg.id = lmgs.group_id
join sms_marketing lm on lm.id = lmg.marketing_id
join pms_sku ls on ls.id = lmg.skuid
where lmgs.del_flag ='0' and lm.del_flag='0' and ls.del_flag = '0'
and now() >= lm.starttime and now() < lm.endtime and lmgs.store_id = #{storeId}

    and lmgs.cate_id = #{cateId}

order by lmgs.sort
limit #{startRowNum},#{pageSize}
