mapper.update:
update sms_marketing set del_flag = '1' where id in

    #{id}

and store_id = #{storeId}

mapper.select:
select * from sms_marketing where del_flag = '0'

    and store_id = #{storeId}

and id = #{id}

select * from sms_marketing
where del_flag = '0'

    and store_id = #{storeId}


    AND name like CONCAT(CONCAT('%', #{name}),'%')



    AND type = #{type}



    AND type in ('1','2','4','5')


    AND cate_id=#{cateId}


    AND now() < starttime


    AND now() >= starttime AND now() < endtime


    AND now() >= endtime


    AND endtime > now()

order by id desc
limit #{startRowNum},#{pageSize}

select count(1) from sms_marketing where del_flag = '0'
and store_id = #{storeId}
AND name like CONCAT(CONCAT('%', #{name}),'%')
AND type = #{type}
AND type in ('1','2','4','5')
AND cate_id=#{cateId}
AND now() < starttime
AND now() >= starttime AND now() < endtime
AND now() >= endtime
AND endtime > now()

select sms_marketing.* ,pms_sku_marketing.skuId as skuid from sms_marketing join pms_sku_marketing on sms_marketing.id = pms_sku_marketing.marketing_id where sms_marketing.starttime <= now() and sms_marketing.endtime > now() and pms_sku_marketing.skuId=#{skuId}
AND type in
#{type}

select count(1) from sms_marketing m join
pms_sku_marketing s on s.marketing_id = m.id
where m.del_flag = '0'
and type = #{type}

    AND m.cate_id = #{cateId}

select m.* from sms_marketing m join
pms_sku_marketing s on s.marketing_id = m.id
where m.del_flag = '0'
and type=#{type}

    AND m.cate_id = #{cateId}

ORDER BY id desc
limit #{startRowNum},#{pageSize}

select count(1) from sms_marketing m join pms_sku_marketing s on s.marketing_id = m.id join pms_sku sku on sku.id = s.skuid where m. sku. sku.shelves_status = '1' and m.endtime> now() and now() >= m.starttime and type in
#{type}
AND m.cate_id = #{cateId}

select m.*,sku.id as skuid,sku.name as skuname,sku.price from sms_marketing m join
pms_sku_marketing s on s.marketing_id = m.id join pms_sku sku on sku.id = s.skuid
where m. sku. sku.shelves_status = '1'
and m.endtime> now() and now() >= m.starttime
and type in

    #{type}


    AND m.cate_id = #{cateId}

order by id desc
limit #{startRowNum},#{pageSize}

select count(1) from sms_marketing
where  now() >= starttime and type = #{type}

    AND cate_id = #{cateId}

select * from sms_marketing
where  now() >= starttime and type = #{type}

    AND cate_id = #{cateId}

order by id desc
limit #{startRowNum},#{pageSize}

select count(1) from sms_marketing lm join pms_sku_marketing lms on lm.id=lms.marketing_id where
!(lm.endtime <#{startTime} || lm.starttime >#{endTime})
lm.endtime >=now()
and lms.skuid in
#{skuId}
and lms.skuid ='-1'
and lm.type IN
#{type}
and lm.del_flag='0'
and lm.id !=#{id}

SELECT lm.*,lmc.money as crowd_funding_money,lmc.already_money as crowd_funding_already_money FROM sms_marketing lm JOIN sms_marketing_crowdfunding lmc on lm.id=lmc.marketing_id WHERE lm.del_flag = '0'
AND lm.store_id = #{storeId}
AND lm.name like CONCAT(CONCAT('%', #{name}),'%')
AND lm.starttime > #{startTime}
AND lm.endtime < #{endTime}
AND lm.starttime > now()
AND lm.starttime < now() AND lm.endtime > now()
AND lm.endtime < now() HAVING crowd_funding_money <=crowd_funding_already_money
AND lm.endtime < now() HAVING crowd_funding_money >crowd_funding_already_money
order by lm.id desc limit #{startRowNum},#{pageSize}

SELECT count(1) FROM (SELECT lmc.money as crowd_funding_money,lmc.already_money as crowd_funding_already_money FROM sms_marketing lm JOIN sms_marketing_crowdfunding lmc on lm.id=lmc.marketing_id WHERE lm.del_flag = '0'
AND lm.store_id = #{storeId}
AND lm.name like CONCAT(CONCAT('%', #{name}),'%')
AND lm.starttime > #{startTime}
AND lm.endtime < #{endTime}
AND lm.starttime > now()
AND lm.starttime < now() AND lm.endtime > now()
AND lm.endtime < now() HAVING crowd_funding_money <=crowd_funding_already_money
AND lm.endtime < now() HAVING crowd_funding_money >crowd_funding_already_money
) ll

SELECT lm.*,lmc.money as crowd_funding_money,lmc.already_money as crowd_funding_already_money,lsi.store_name as store_name FROM sms_marketing lm JOIN sms_marketing_crowdfunding lmc on lm.id=lmc.marketing_id join t_store_info lsi on lm.store_id = lsi.id WHERE lm. lsi.del_flag = '0'
AND lsi.store_name = #{storeName}
AND lm.name like CONCAT(CONCAT('%', #{name}),'%')
AND lm.starttime > #{startTime}
AND lm.endtime < #{endTime}
AND lm.starttime > now()
AND lm.starttime < now() AND lm.endtime > now()
AND lm.endtime < now() HAVING crowd_funding_money <=crowd_funding_already_money
AND lm.endtime < now() HAVING crowd_funding_money >crowd_funding_already_money
order by lm.id desc limit #{startRowNum},#{pageSize}

