mapper.select:
select lmp.*,lm.store_id from sms_marketing_presale lmp join sms_marketing lm on lm.id = lmp.marketing_id join pms_sku ls on ls.id = lmp.skuid where lm. ls. now() >= lm.starttime and now() < lm.endtime and (select count(1) as num from sms_marketing_presale_show lmps where lmps.presale_id = lmp.id and lmps.store_id = #{storeId} and lmps.del_flag = '0') = 0
and lm.store_id = #{storeId}
and ls.name like CONCAT(CONCAT('%', #{name}),'%')
and ls.sku_no = #{skuNo}
order by lmp.id desc limit #{startRowNum},#{pageSize}

select count(1) from sms_marketing_presale lmp join sms_marketing lm on lm.id = lmp.marketing_id join pms_sku ls on ls.id = lmp.skuid where lm. ls. now() >= lm.starttime and now() < lm.endtime and (select count(1) as num from sms_marketing_presale_show lmps where lmps.presale_id = lmp.id and lmps.store_id = #{storeId} and lmps.del_flag = '0') = 0
and lm.store_id = #{storeId}
and ls.name like CONCAT(CONCAT('%', #{name}),'%')
and ls.sku_no = #{skuNo}

select lmp.* from sms_marketing_presale lmp
join sms_marketing lm on lm.id = lmp.marketing_id
where lm. lmp.id = #{id}

    and lm.store_id = #{storeId}
