mapper.select:
select * from ums_member
where del_flag = '0'

    AND username like CONCAT(CONCAT('%', #{username}),'%')



    AND firstname like CONCAT(CONCAT('%', #{firstname}),'%')



    AND lastname like CONCAT(CONCAT('%', #{lastname}),'%')



    AND nationality =#{nationality}


    AND nationality_id =#{nationalityId}



    AND idtype =  #{idtype})



    AND mobile = #{mobile}


    AND areacode = #{areacode}



    AND email = #{email}



    AND is_mobile_verification = #{isMobileVerification}



    AND is_email_verification = #{isEmailVerification}



    AND realname_screen_status = #{realnameScreenStatus}



    AND allow_fiat_withdraw = #{allowFiatWithdraw}



    AND allow_crypto_withdraw = #{allowCryptoWithdraw}



    AND status = #{status}



    AND type = #{type}



    AND DATE(create_time) = #{createTime}



    and belone_store_id = #{beloneStoreId}


order by create_time desc
limit #{startRowNum},#{pageSize}

select count(1) from ums_member
where del_flag = '0'

    AND username like CONCAT(CONCAT('%', #{username}),'%')



    AND firstname like CONCAT(CONCAT('%', #{firstname}),'%')


    AND lastname like CONCAT(CONCAT('%', #{lastname}),'%')



    AND nationality  = #{nationality}



    AND nationality_id  = #{nationalityId}



    AND mobile = #{mobile}


    AND areacode = #{areacode}



    AND email = #{email}



    AND is_mobile_verification = #{isMobileVerification}



    AND is_email_verification = #{isEmailVerification}



    AND is_google_verification = #{isGoogleVerification}



    AND realname_screen_status = #{realnameScreenStatus}



    AND status = #{status}



    AND type = #{type}



    AND DATE(create_time) = #{createTime}


    and belone_store_id = #{beloneStoreId}

SELECT lc.id,lc.username,lc.nickname,lc.firstname, lc.lastname,lc.store_id,lc.`type`,lc.`status`,lc.last_login_time,lc.create_time, custoer_role.role_id,custoer_role.`name` FROM ums_member AS lc JOIN (SELECT lcr.customer_id,lcr.role_id,lsr.name FROM ums_member_role as lcr JOIN t_store_role as lsr ON lcr.role_id=lsr.id WHERE lsr.store_id=#{storeId}) AS custoer_role ON lc.id=custoer_role.customer_id where store_id=#{storeId} AND `type`=3
AND username like CONCAT(CONCAT('%', #{name}),'%')
ORDER BY create_time DESC limit #{startRowNum},#{pageSize}

mapper.update:
UPDATE ums_member SET

    nickname=#{customer.nickname},gender=#{customer.gender},birthday=#{customer.birthday},firstname=#{customer.firstname},lastname=#{customer.lastname}


    image=#{customer.image}


    monthly_income=#{customer.monthlyIncome},marriage_status=#{customer.marriageStatus},cardid=#{customer.cardid}

WHERE id=#{customer.id}

mapper.select:
 select DATE(create_time) as 'newCustomerTime',count(1) as 'newCustomerNum' from ums_member
 where DATE(create_time) between #{startTime,jdbcType=TIMESTAMP} and #{endTime,jdbcType=TIMESTAMP}
group by DATE(create_time) order by create_time

 select count(1) from (select count(1) from ums_member where DATE(create_time)
 between #{startTime,jdbcType=TIMESTAMP} and #{endTime,jdbcType=TIMESTAMP}
group by DATE(create_time)) as num

 select DATE(create_time) as 'newCustomerTime',count(1) as 'newCustomerNum' from ums_member
 where DATE(create_time) between #{startTime,jdbcType=TIMESTAMP} and #{endTime,jdbcType=TIMESTAMP}
group by DATE(create_time) order by create_time
 limit #{startRowNum},#{pageSize}

