mapper.select:
select (select count(1) from t_store_info where
store_name=#{storeName}

    and id !=#{storeId}
) >0

select(select count(1) from t_store_info where
company_name=#{companyName}

    and id !=#{storeId}
)>0

select id,store_name,company_address,avatar_picture,company_phone,longitude,latitude ,(st_distance (point (longitude, latitude),point(#{longitude},#{latitude}) ) / 0.0111) AS distance from t_store_info where del_flag='0' and status='2' and effective_time >now() and type !='0'
HAVING distance < #{distance}
ORDER BY distance

SELECT COUNT(1) FROM t_store_info
LEFT JOIN ums_member on t_store_info.id=ums_member.store_id
WHERE t_store_info.del_flag='0' AND ums_member.type='2' and t_store_info.type !='0'

    AND ums_member.mobile=#{mobile}


    AND t_store_info.company_name = #{companyName}


    AND t_store_info.store_name = #{storeName}

SELECT t_store_info.* ,ums_member.mobile customer_mobile FROM t_store_info
LEFT JOIN ums_member on t_store_info.id=ums_member.store_id
WHERE t_store_info.del_flag='0' AND ums_member.type='2' and t_store_info.type!='0'

    AND ums_member.mobile=#{mobile}


    AND t_store_info.company_name = #{companyName}


    AND t_store_info.store_name = #{storeName}

ORDER BY t_store_info.create_time DESC
limit #{startRowNum},#{pageSize}

select lsi.*,(st_distance (point (lsi.longitude, lsi.latitude),point(#{longitude},#{latitude}) ) / 0.0111) AS distance, IFNULL(lc.comment_count,0) comment_count,IFNULL(lsale.sale_count,0) sale_count from t_store_info lsi left JOIN (select COUNT(1) comment_count,store_id comment_store_id from pms_comment GROUP BY store_id ) lc on lsi.id=lc.comment_store_id left JOIN (select sum(los.num) sale_count,lo.store_id order_store_id from oms_order lo,oms_order_sku los where store_id !=0 and lo.status in ('4','6','7') GROUP BY lo.store_id) lsale on lsi.id=lsale.order_store_id where lsi.status='2' and lsi.effective_time > now() and lsi.del_flag='0' and lsi.type = '0'
HAVING distance < #{distance}
ORDER BY distance
